import React from "react";
import { Button, Container,Col } from "react-bootstrap";



class MainPage extends React.Component {
  state = {
    isLoading: true,
    isError: false,
    cities: [],
    input: "",
  };

  async getLocations(query) {
    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${query}&APPID=3e93bd34cafe899e1166a35a9efa51fc&units=metric`
      );
      if (response.ok) {
        const data = await response.json();
        console.log(data);
        this.setState({ ...this.state, cities: data });
        console.log(this.state);
      } else {
        alert("Error fetching results");
      }
    } catch (error) {
      console.log(error);
    }
  }

  componentDidMount() {
    this.getLocations("Berlin");
  }

  render() {
    return (
      <>
        <Container id="container">

          <input
            type="text"
            value={this.state.input}
            onChange={(e) => {
              this.setState({ ...this.state, input: e.target.value });
            }}
            placeholder="Enter location..."
          />
          <Button
            id="searchbutton"
            onClick={() => {
              this.getLocations(this.state.input);
            }}
          >
            Search
          </Button>

          <h2>Current Weather</h2>

          <div>
            <div className="flexweather">
              <h1 id="h1">{Math.round(this.state.cities?.main?.temp)}C</h1>{" "}
              {this.state.cities?.weather?.map((weather) => (
                <img
                  id="weatherlogo"
                  attribute
                  title={weather.description}
                  src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`}
                  alt=""
                ></img>
              ))}
            </div>
            <p id="city">{this.state.cities.name}</p>
            <Col>
              <h4>
                {"Local Time: "}
                {new Date().setTime(
                  new Date().getUTCHours() + this.state.timezone / 60 / 60
                )}{" "}
                : {new Date().getMinutes()} UTC: {this.state.timezone / 60 / 60}
              </h4>
            </Col>

            <Col xs={4}>
              <p>Wind: {this.state.wind.speed}m/s</p>
              <p>Direction: {this.state.wind.deg}deg</p>
            </Col>
            <Col xs={4}>
              <p>Visibility: {this.state.visibility} m</p>
              <p> Clouds: {this.state.clouds.all}%</p>
            </Col>



          </div>

        </Container>
      </>
    );
  }
}

export default MainPage;
